    private static char[][] allXBoard(int n, int m) {
        char[][] board = new char[n][m];
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < m; j++) {
                board[i][j] = 'X';
            }
        }
        return board;
    }

    private static char[][] allOBoard(int n, int m) {
        char[][] board = new char[n][m];
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < m; j++) {
                board[i][j] = 'O';
            }
        }
        return board;
    }

    public class Solution {
        public void solve(char[][] board) {
            if (board == null || board.length < 3) return;
            int N = board.length;
            int M = board[0].length;

            Queue<Point> q = new LinkedList<>();

            // top and bottom rows
            for (int j = 0; j < M; j++) {
                if (board[0][j] == 'O')
                    q.add(new Point(0, j));
                if (board[N - 1][j] == 'O')
                    q.add(new Point(N - 1, j));
            }
            // left and right columns
            for (int i = 1; i < N - 1; i++) {
                if (board[i][0] == 'O')
                    q.add(new Point(i, 0));
                if (board[i][M - 1] == 'O')
                    q.add(new Point(i, M - 1));
            }

            dfs(q, board, N, M);

            // go through all elements and restore 'a's back to O - they are not surrounded
            // those that are currently 'O' - are surrounded, therefore set them to 'X'
            for (int i = 0; i < N; i++) {
                for (int j = 0; j < M; j++) {
                    if (board[i][j] == 'O')
                        board[i][j] = 'X';
                    if (board[i][j] == 'a')
                        board[i][j] = 'O';
                }
            }
        }

        /**
         * marks the current element as 'a' (it means that it's not surrounded)
         */
        private void dfs(Queue<Point> q, char[][] board, int N, int M) {
            while (!q.isEmpty()) {
                Point p = q.remove();
                int i = p.i;
                int j = p.j;
                board[i][j] = 'a';
                // up
                if (i != 0 && board[i - 1][j] == 'O')
                    q.add(new Point(i - 1, j));
                // down
                if (i != N - 1 && board[i + 1][j] == 'O')
                    q.add(new Point(i + 1, j));
                // left
                if (j != 0 && board[i][j - 1] == 'O')
                    q.add(new Point(i, j - 1));
                // right
                if (j != M - 1 && board[i][j + 1] == 'O')
                    q.add(new Point(i, j + 1));
            }
        }

        private class Point {
            int i, j;

            Point(int i, int j) {
                this.i = i;
                this.j = j;
            }
        }
    }
