    private static char[][] allXBoard(int n, int m) {
        char[][] board = new char[n][m];
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < m; j++) {
                board[i][j] = 'X';
            }
        }
        return board;
    }

    public class Solution {
        public void solve(char[][] board) {
            if (board == null || board.length < 3) return;
            int N = board.length;
            int M = board[0].length;

            // top and bottom rows
            for (int j = 0; j < M; j++) {
                dfs(board, 0, j, N, M);
                dfs(board, N - 1, j, N, M);
            }
            // left and right columns
            for (int i = 1; i < N - 1; i++) {
                dfs(board, i, 0, N, M);
                dfs(board, i, M - 1, N, M);
            }

            // go through all elements and restore 'a's back to O - they are not surrounded
            // those that are currently 'O' - are surrounded, therefore set them to 'X'
            for (int i = 0; i < N; i++) {
                for (int j = 0; j < M; j++) {
                    if (board[i][j] == 'O')
                        board[i][j] = 'X';
                    if (board[i][j] == 'a')
                        board[i][j] = 'O';
                }
            }
        }

        /**
         * marks the current element as 'a' (it means that it's not surrounded)
         */
        private void dfs(char[][] board, int i, int j, int N, int M) {
            if (board[i][j] != 'O') return;
            board[i][j] = 'a';
            // up
            if (i != 0 && board[i - 1][j] == 'O')
                dfs(board, i - 1, j, N, M);
            // down
            if (i != N - 1 && board[i + 1][j] == 'O')
                dfs(board, i + 1, j, N, M);
            // left
            if (j != 0 && board[i][j - 1] == 'O')
                dfs(board, i, j - 1, N, M);
            // right
            if (j != M - 1 && board[i][j + 1] == 'O')
                dfs(board, i, j + 1, N, M);
        }
    }
